import React, { useState } from 'react';
import './Header.css';
import logo from '../Header/Instagram_logo.png';
import SearchIcon from '../../icons/SearchIcon';
import Home from '../../icons/Home';
import dmIcon from '../../icons/dm-message.jpeg';
import plusIcon from '../../icons/plus.png';
import likeIcon from '../../icons/like.png';

const Header = () => {
  const [showSearchIcon, setShowSearchIcon] = useState(true);

  const handleShowSearchIcon = () => {
    setShowSearchIcon(!showSearchIcon);
  };

  return (
    <header>
      <div className="header__inner">
        <div>
          <img className="header__logo" src={logo} alt="header-logo" />
        </div>
        <div className="header-section">
          <div className="header__search">
            {showSearchIcon ? <SearchIcon /> : null}
            <input
              onBlur={handleShowSearchIcon}
              placeholder="Поиск"
              className="header__input"
              type="text"
            />
          </div>
          <ul className="header__list">
            <li className="header__item">
              <Home />
            </li>
            <li className="header__item">
              <img className="dm-icon" src={dmIcon} alt="direct-message-icon" />
            </li>
            <li className="header__item">
              <img className="plus-icon" src={plusIcon} alt="direct-message-icon" />
            </li>
            <li className="header__item">
              <img className="like-icon" src={likeIcon} alt="direct-message-icon" />
            </li>
          </ul>
        </div>
      </div>
    </header>
  );
};

export default Header;
