import React from 'react';
import masterpiece from '../Posts/post-photo1.jpeg';
import PostFooter from '../Posts/PostFooter';
import './Modal.css';

const Modal = ({ children, handleShowModal }) => {
  return (
    <div className="fixed-modal">
      <div className="modal__cross" onClick={handleShowModal}>
        <span></span>
        <span></span>
      </div>
      <div className="modal modal-wrapper">
        <div className="modal-content">
          <img className="modal-img" src={masterpiece} alt="" />
          <div className="modal-comments">
            {children}
            <PostFooter />
          </div>
        </div>
      </div>
    </div>
  );
};
export default Modal;
