import React from 'react';
import NickName from '../NickName/NickName';
import masterpiece from '../Posts/post-photo1.jpeg';
import PostFooter from '../Posts/PostFooter';
import PostHeader from '../Posts/PostHeader';
import StoriesUser from '../Stories/StoriesUser';
import './Modal.css';

const Modal = ({ comment }) => {
  console.log(comment);
  return (
    <div className="fixed-modal">
      <div className=""></div>
      <div className="modal modal-wrapper">
        <div className="modal-content">
          <img className="modal-img" src={masterpiece} alt="" />
          <div className="modal-comments">
            <PostHeader />
            {comment.map((c) => (
              <div className="modal__comments-inner">
                <StoriesUser size={true} />
                <NickName nameOfUser={c.senderName} />
                <div className="modal__comments-info">{c.senderCommentText}</div>
              </div>
            ))}
            <PostFooter />
          </div>
        </div>
      </div>
    </div>
  );
};
export default Modal;
